import React, { useEffect, useRef } from 'react';
import VanillaTilt from 'vanilla-tilt';


function Tilt(props) {
  const { options, ...rest } = props;
  const tilt = useRef(null);

  useEffect(() => {
    VanillaTilt.init(tilt.current, options);
  }, [options]);

  return <div ref={tilt} {...rest} />;
}



const ChatList = () => {

  const options = {
    scale: 1.01,
    speed: 1000,
    max: 5
  };

  return (
    <div className='chatlist_bg'>
        <div className="login_bgm_b">
          <video className="login_bgm" autoPlay muted loop>
          <source src='videos/mainmain2.mp4' type='video/mp4' />
        </video>
        </div>
        <div className='chatlist_box'>
          <div className='chatlist_box_in'>
            <div className='chatlist_list_header'><h1>채팅창 리스트</h1></div>
            <hr/>
            <Tilt options={options} className='chat_list_contents'><div className='chat_list_pro_img'></div><p className='chat_list_name'>김춘자</p><p className='chat_con_miri'>대화내용입니다.</p></Tilt>
            <Tilt options={options} className='chat_list_contents'><div className='chat_list_pro_img'></div><p className='chat_list_name'>이향자</p><p className='chat_con_miri'>대화내용입니다.</p></Tilt>
            <Tilt options={options} className='chat_list_contents'><div className='chat_list_pro_img'></div><p className='chat_list_name'>추정화</p><p className='chat_con_miri'>대화내용입니다.</p></Tilt>
            <Tilt options={options} className='chat_list_contents'><div className='chat_list_pro_img'></div><p className='chat_list_name'>이영자</p><p className='chat_con_miri'>대화내용입니다.</p></Tilt>
          </div>
        </div>

    </div>
  )
}

export default ChatList
