import React from "react";
import ReactPlayer from "react-player";
import Title from 'react-vanilla-tilt';

const Login = () => {

  const options = {
    scale: 1.2,
    speed: 1000,
    max: 30
  };


  const onChange = (e) => {
      console.log(e.target);
  }

  return (
    <div className='login_page'>
          <div className="login_bgm_b">
            <video className="login_bgm" autoPlay muted loop>
              <source src='videos/mainmain.mp4' type='video/mp4' />
            </video>
          </div>
          <Tilt options={options} className='login_box'>
            <div className="login_box_header_name">
              <h1>Log in</h1>
            </div>
            <input
            className="id_box"
            id="id"
            name="id"
            placeholder="아이디.."
            onChange={onChange}
            ></input>

            <input
            className="ps_box"
            id="password"
            name="password"
            type="password"
            placeholder="비밀번호.."
            onChange={onChange}
            ></input>
            <a href="#" className="login_wan_btn">Login</a>
            <a href='#' className="find_ps_id">비밀번호 찾기 / 아이디 찾기</a>

        </Tilt> 
    </div>
  )
}

export default Login
