// import Swiper core and required modules
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper/modules';

import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

const Baenner = () => {
  /**해당 배너 에서는 슬라이프라는 라이브 러리가 쓰였습니다. */
  /* npm i swiper 로 다운로드 후 {Swiper, SwiperSlide } 로 import 합니다.
  npm install --save sass */

  return (
    <Swiper
      // install Swiper modules
      modules={[Navigation, Pagination, Scrollbar, A11y]}
      spaceBetween={50}
      slidesPerView={3}
      navigation
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log('slide change')}
    >
      <SwiperSlide className='box'>Slide 1</SwiperSlide>
      <SwiperSlide className='box'>Slide 2</SwiperSlide>
      <SwiperSlide className='box'>Slide 3</SwiperSlide>
      <SwiperSlide className='box'>Slide 4</SwiperSlide>
      ...
    </Swiper>
  )
}

export default Baenner
