import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';

const ChatBox = () => {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  const socketRef = useRef();

  useEffect(() => {
    socketRef.current = io.connect('http://localhost:3005'); // 서버 URL

    socketRef.current.on('message', ({ message }) => {
      setChat(oldChat => [...oldChat, message]);
    });

    return () => socketRef.current.disconnect();
  }, []);

  const onTextChange = (event) => {
    setMessage(event.target.value);
  };

  const onMessageSubmit = (event) => {
    event.preventDefault();

    const messageObject = {
      message,
    };

    socketRef.current.emit('send message', messageObject);
    setMessage('');
  };

  return (
    <div className='chatbox_bg'>
        <div className='chatbox_box'>
            <div className='chatbox_contents'>
              {chat.map((message, index) => (
                <div key={index}>{message}</div>
              ))}
            </div>
            <div className='chatbox_input'>
                <form className='chatbox_input_in' onSubmit={onMessageSubmit}>
                  <input className='chat_input_text' type="text" value={message} onChange={onTextChange} />
                  <button type="submit" className="chat_send_btn">보내기</button>
                </form>
            </div>
        </div>
    </div>
  );
};

export default ChatBox;
