import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Pagination, Autoplay } from "swiper/core";
import "swiper/css";
import "swiper/css/pagination";

SwiperCore.use([Pagination, Autoplay]);


const Baenner = () => {
  /**해당 배너 에서는 슬라이프라는 라이브 러리가 쓰였습니다. */
  /* npm i swiper 로 다운로드 후 {Swiper, SwiperSlide } 로 import 합니다. */
  const items = [
    { src: "/assets/imgs/good.jpg" },
    { src: "/assets/imgs/genius.jpg" },
  ];

  return (
    <div className="main-wrap">
        <Swiper
          style={{
            width: "335px",
            height: "170px",
            backgroundColor: "#FFF5F1",
            borderRadius: "12px",
          }}
          spaceBetween={8}
          initialSlide={1}
          centeredSlides={true}
          pagination={{
            clickable: true,
          }}
          autoplay={{ delay: 3000 }}
        >
          <SwiperSlide>슬라이더1</SwiperSlide>
          <SwiperSlide>슬라이더2</SwiperSlide>
          <SwiperSlide>슬라이더3</SwiperSlide>
        </Swiper>
      </div>
  )
}

export default Baenner
